import React from "react";
import DateRangePicker from "react-daterange-picker";
import "react-daterange-picker/dist/css/react-calendar.css";
import originalMoment from "moment";
import { extendMoment } from "moment-range";
const moment = extendMoment(originalMoment);





class DatePicker extends React.Component {
  constructor(props, context) {
    super(props, context);
    const today = moment();
    this.state = {
      value: moment.range(today.clone(), today.clone())
    };
  }

  onSelect = (value, states) => {
    this.setState({ value, states });
  };

  onToggle = () => {
    this.setState({ isOpen: !this.state.isOpen });
  };

  renderSelectionValue = () => {
    return (
      <div>
        {this.state.value.start.format("DD-MM-YYYY")}
        {" - "}
        {this.state.value.end.format("DD-MM-YYYY")}
      </div>
    );
  };

  stateDefinitions = {
    available: {
      color: null,
      label: 'Beschikbaar',
    },
    unavailable: {
      selectable: false,
      color: '#78818b',
      label: 'Gereserveerd',
    },
  };

  dateRanges = [
    {
      state: 'unavailable',
      range: moment.range(
        moment().add(4, 'weeks'),
        moment().add(3, 'weeks').add(5, 'days')
      ),
    },
  ];

  render() {
    return (
      <div>
          <DateRangePicker
            value={this.state.value}
            onSelect={this.onSelect}
            singleDateRange={true}
            numberOfCalendars={2}
            selectionType='range'
            minimumDate={new Date()}
            showLegend={true}
            stateDefinitions={this.stateDefinitions}
            dateStates={this.dateRanges}
            defaultState="available"
            locale={moment().locale('nl')}
            firstOfWeek={1}
            />
          <div>{this.renderSelectionValue()}</div>
      </div>
    );
  }
}

export default DatePicker;
