import {Form, Formik, Field, ErrorMessage, formikHelpers} from 'formik'
import {object, string} from 'yup';
import axios from 'axios'
import { useState } from 'react'

const initialValues = {
    voornaam: '',
    achternaam: '',
    email: '',
    bericht: '',
    responded: false
}

const ContactForm = () => {
    const [message, setMessage] = useState('');

    return(
        <div>
            <Formik 
                validationSchema={
                    object({
                        voornaam: string().required(),
                        achternaam: string().required(),
                        email: string().required(),
                        bericht: string().required()
                    })
                }
                initialValues={initialValues} 
                onSubmit={(values, formikHelpers)=> {
                    console.log(values)
                    axios.post("https://wdev.be/wdev_anneleen/eindwerk/api/contacts", values)
                        .then(function (response) {
                            setMessage("Jouw vraag is verzonden!");
                        })
                        .catch(function (error) {
                            setMessage("Oeps! Er liep iets fout!");
                        });                        
                }}>
                {({values, errors, isSubmitting}) => (
                    <Form>
                        <p>{message}</p>

                        <Field className="form-input" name="voornaam" type="text" placeholder="voornaam" className="inputtype-style-2"></Field>
                        <div><ErrorMessage name="voornaam"></ErrorMessage></div>

                        <Field className="form-input"  name="achternaam" type="tex" placeholder="achternaam" className="inputtype-style-2"></Field>
                        <div><ErrorMessage name="achternaam"></ErrorMessage></div>

                        <Field className="form-input"  name="email" type="email" placeholder="email" className="inputtype-style-2"></Field>
                        <div><ErrorMessage name="email"></ErrorMessage></div>

                        <Field className="form-input"  name="bericht" as="textarea" placeholder="bericht" className="inputtype-style-2"></Field>
                        <div><ErrorMessage name="beticht"></ErrorMessage></div>

                        <button type="submit" className="button-style-1" disabled={isSubmitting}>Verzenden</button>
                    </Form>
                )}
            </Formik>
            <style jsx>{`
                form {
                    display: flex;
                    flex-direction: column;
                    margin-top: 40px;
                }
                form-input {
                    background-color: transparent;
                    padding: 10px 20px;
                    margin: 10px 0;
                    border: none;
                    border-bottom: 1px solid #1F1F1F;
                    font-family: 'Hind', sans-serif;
                }

                button {
                    width: 60%;
                    margin: 20px auto 0 auto;
                }
            `}</style>                
        </div>
    ) 
};  

export default ContactForm;
