import Layout from "../components/Layout"
import Head from 'next/head'
import axios from "axios"
import logoTitleImage from '../images/logo_title.png'
import {useState, useEffect, useSWR} from "react"
import kamerImage from '../images/kamer_mainimg.jpg'
import homeImage from '../images/home_main.jpg'



const Kamer = () => {
    //const [data, setData] = useState([]);
    // useEffect(() => {
    //     async function loadData(){
    //         const response = await axios("https://wdev.be/wdev_anneleen/eindwerk/api/kamers");
    //         setData(response.data['hydra:member']);
    //         console.log(response.data['hydra:member']);
    //     }
    //     loadData();
    // },[]);

    return (
        <div>
            <Head>
                <title>La Garchere - Kamers</title>
                <link rel="icon" href={logoTitleImage} type="image/icon type"/>
            </Head>
            <Layout>
            <div className="kamer-container">
                <section>
                    <div className="kamer-section">
                        <div className="kamer-block-one">
                            <h1 className="heading-style-1">Luxe</h1>
                            <p className="ondertitel">2 personen, 10m2</p>
                            <p className="omschrijving">Op de begane grond bevindt zich eveneens tweepersoonsslaapkamer met badkamer ensuite en toegang tot een achterliggend terrasje. Christian Lacroix gaf de inspiratie voor de inrichting. Taften gordijnen en grote kussens stralen luxe en overdaad uit.</p>
                            <p>Prijs hoogseizoen: 200 euro/nacht</p>
                            <p>Prijs laagseizoen: 150 euro/nacht</p>
                            <h2 className="heading-style-2">Diensten</h2>
                            <ul>
                                <li>Gratis wifi</li>
                                <li>Linnengoed</li>
                                <li>Gratis gebruik van spafaciliteiten</li>
                                <li>Gratis parkeren rond het domein</li>
                            </ul>
                        </div>
                        <div className="kamer-block-two">
                            <img src={kamerImage} alt="jow"/>
                            <a>Bekijk alle foto's</a>
                        </div>
                    </div>
                </section>
                <section>
                    <div className="kamer-section">
                        <div className="kamer-block-one">
                            <h1 className="heading-style-1">Luxe</h1>
                            <p className="ondertitel">2 personen, 10m2</p>
                            <p className="omschrijving">Op de begane grond bevindt zich eveneens tweepersoonsslaapkamer met badkamer ensuite en toegang tot een achterliggend terrasje. Christian Lacroix gaf de inspiratie voor de inrichting. Taften gordijnen en grote kussens stralen luxe en overdaad uit.</p>
                            <p>Prijs hoogseizoen: 200 euro/nacht</p>
                            <p>Prijs laagseizoen: 150 euro/nacht</p>
                            <h2 className="heading-style-2">Diensten</h2>
                            <ul>
                                <li>Gratis wifi</li>
                                <li>Linnengoed</li>
                                <li>Gratis gebruik van spafaciliteiten</li>
                                <li>Gratis parkeren rond het domein</li>
                            </ul>
                        </div>
                        <div className="kamer-block-two">
                            <img src={homeImage} alt="jow"/>
                            <a>Bekijk alle foto's</a>
                        </div>
                    </div>
                </section>
            </div>
            </Layout>
            <style jsx>{`
            p {
                line-height: 1.5;
            }
            .kamer-container {
                margin: 20px;
            }

            .kamer-section {
                background-color: #1F1F1F;
                color: white;
            }

            .heading-style-2,
            .heading-style-1 {
                color: white;
            }

            .kamer-block-one {
                padding: 50px 5% 20px 5%;
            }

            .ondertitel {
                font-size: 0.8rem;
                margin-bottom: 20px;
            }

            .omschrijving {
                margin-bottom: 20px;
            }

            .heading-style-2 {
                margin-top: 40px;
            }

            .kamer-block-two img {
                height: 400px;
                width: 80%;
                object-fit: cover;
            }

            li {
                list-style-type: circle;
                margin-left: 40px;
                line-height: 1.8;
            }

            @media (min-width: 40em) {
                .kamer-block-one {
                    padding: 70px 10% 20px 10%;
                }

                .kamer-block-two img {
                    width: 60%;
                    padding: 20px 10% 20px 10%;
                }
            }

            @media (min-width: 45em) {
                .kamer-section {
                    display: grid;
                    grid-template-columns: auto auto;
                    grid-column-gap: 20px;
                }

                .kamer-block-one {
                    max-width: 400px;
                    padding-bottom: 80px;
                }

                .kamer-block-two {
                    height: 100%;
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;
                    padding-right: 40px;
                }

                .kamer-block-two img {
                    width: 100%;
                    padding: 0;
                }

                .kamer-block-two a {
                    font-family: 'Abhaya Libre', serif;
                    text-transform: uppercase;
                    border-bottom: 1px solid white;
                    font-weight: 600;
                    letter-spacing: 1px;
                    margin-top: 20px;
                    color: white;
                    font-size: 1.2rem;
                }
            }

            @media (min-width: 58em) {
                .kamer-section {
                    margin: 3%;
                }

                .kamer-block-one {
                    max-width: 400px;
                }

                .kamer-block-two {
                    max-width: 400px;
                }

            }

            @media (min-width: 62em) {
                .kamer-section {
                    position: relative;
                    margin-bottom: 100px;
                }

                .kamer-block-one {
                    width: 400px;
                }

                .kamer-block-two {
                    position: absolute;
                    right: 0;
                    top: 80px;

                }

                .kamer-block-two img {
                    height: 600px;
                    width: 400px;
                    object-fit: cover;
                  }

                  .kamer-block-two a {
                      color: #1F1F1F;
                      border-bottom 1px solid #1F1F1F;
                  }

            }

            @media (min-width: 73em) {
                .kamer-section {
                    margin: 0 10% 100px 10%;
                }

                .kamer-block-two img {
                    height: 600px;
                    width: 400px;
                    object-fit: cover;
                  }

            }



            `}</style>
        </div>
    )
  }

  export default Kamer
