import { parseCookies, destroyCookie } from 'nookies'


export const getJwt = (ctx) => {
    const cookies = parseCookies(ctx)
    const jwt = cookies.jwtToken;
    return jwt;
}

export function logout()  {
    destroyCookie(null, "jwtToken");
    Router.push("/login");
}
