import React, { useState } from "react";
import DateRangePicker from "react-daterange-picker";
import "react-daterange-picker/dist/css/react-calendar.css";
import originalMoment from "moment";
import { extendMoment } from "moment-range";
const moment = extendMoment(originalMoment);


function DatePicker () {
  const today = moment();
  const [startValue, setStartValue] = useState(today)
  const [endValue, setEndValue] = useState(today)


  function onSelect (value, states) {
    this.setState({ value, states });
  };

  const renderSelectionValue = () => {
    return (
      <div>
        {value.start.format("DD-MM-YYYY")}
        {" - "}
        {value.end.format("DD-MM-YYYY")}
      </div>
    );
  };

    return (
      <div>
          <DateRangePicker
            value={value}
            onSelect={onSelect}
            singleDateRange={true}
          />
          <div>{renderSelectionValue()}</div>
      </div>
    );

}

export default DatePicker;
