import {Form, Formik, Field, ErrorMessage, formikHelpers} from 'formik'
import {object, string} from 'yup';
import axios from 'axios'
import { useState } from 'react'

const initialValues = {
    voornaam: '',
    achternaam: '',
    email: '',
    bericht: ''
}

const ContactForm = () => (
    <div>
        <Formik 
            validationSchema={
                object({
                    voornaam: string().required(),
                    achternaam: string().required(),
                    email: string().required(),
                    bericht: string().required()
                })
            }
            initialValues={initialValues} 
            onSubmit={(values, formikHelpers)=> {
                axios.post("https://wdev.be/wdev_anneleen/eindwerk/api/contacts", values)
                            .then(function (response) {
                                setMessage("Joepie! Je ontvangt nu onze nieuwsbrieven!");
                            })
                            .catch(function (error) {
                                setMessage("Oeps! Er liep iets fout!");
                            });                        
                    }}>
                    {({values, errors, isSubmitting}) => (
                        <Form>
                            <Field name="voornaam" type="text" placeholder="voornaam" className="inputtype-style-one"></Field>
                            <div><ErrorMessage name="voornaam"></ErrorMessage></div>
                            <Field name="achternaam" type="tex" placeholder="achternaam" className="inputtype-style-one"></Field>
                            <div><ErrorMessage name="achternaam"></ErrorMessage></div>
                            <Field name="email" type="email" placeholder="email" className="inputtype-style-one"></Field>
                            <div><ErrorMessage name="email"></ErrorMessage></div>
                            <Field name="bericht" type="text" placeholder="bericht" className="inputtype-style-one"></Field>
                            <div><ErrorMessage name="beticht"></ErrorMessage></div>
                            <button type="submit" className="button-style-1" disabled={isSubmitting}>Inschrijven</button>
                            <p className="message-subscribe">{message}</p>
                        </Form>
                    )}
                </Formik>
         <style jsx>{`
            form {
                display: flex;
                flex-direction: column;
                margin-top: 40px;
            }
            input, textarea {
                background-color: transparent;
                padding: 10px 20px;
                margin: 10px 0;
                border: none;
                border-bottom: 1px solid #1F1F1F;
                font-family: 'Hind', sans-serif;
            }
            textarea {
                min-height: 80px;
            }
            button {
                width: 60%;
                margin: 20px auto 0 auto;
            }
        `}</style>                
    </div>
);  

export default ContactForm;
